import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function StaffDashboard() {
  const location = useLocation();
  const navigate = useNavigate();
  const [staff, setStaff] = useState(null);

  useEffect(() => {
    // Get staff data passed through navigation or from localStorage
    const staffData = location.state?.staff || JSON.parse(localStorage.getItem('staff'));
    if (staffData) {
      setStaff(staffData);
      localStorage.setItem('staff', JSON.stringify(staffData)); // store for refresh
    } else {
      alert('Unauthorized access');
      navigate('/staff-login');
    }
  }, [location, navigate]);

  return (
    <div style={{ textAlign: 'center', marginTop: '100px' }}>
      {staff ? (
        <>
          <h1>Welcome, {staff.name}</h1>
          <p>User ID: {staff.id}</p>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default StaffDashboard;
