import React, { useEffect, useState } from 'react';
import { collection, getDocs } from 'firebase/firestore';
import { db } from '../firebase';
import { useNavigate } from 'react-router-dom';

function AdminUnverifiedApplications() {
  const [unverified, setUnverified] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUnverified = async () => {
      const appliedSnapshot = await getDocs(collection(db, 'Applied'));
      const apps = [];

      for (const docSnap of appliedSnapshot.docs) {
        const appliedData = docSnap.data();
        const appliedId = docSnap.id;

        if (appliedData.verify === 'false') {
          // Get matching application from applications collection
          const appSnap = await getDocs(collection(db, 'applications'));
          let applicationData = null;

          appSnap.forEach((a) => {
            if (a.id === appliedData.applicationId) {
              applicationData = a.data();
            }
          });

          apps.push({
            appliedId,
            applied: appliedData,
            application: applicationData,
            staffId: 'admin',
          });
        }
      }

      setUnverified(apps);
    };

    fetchUnverified();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Unverified Applications</h2>

      {unverified.length === 0 ? (
        <p>No unverified applications found.</p>
      ) : (
        <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
          {unverified.map((item, index) => (
            <div key={index} style={{ padding: '15px', border: '1px solid #ccc', borderRadius: '10px' }}>
              <p><strong>Applicant Name:</strong> {item.applied.name}</p>
              <p><strong>Phone:</strong> {item.applied.phone}</p>
              <p><strong>Occupation:</strong> {item.applied.occupation}</p>
              <p><strong>Application ID:</strong> {item.appliedId}</p>
              <button
                onClick={() => navigate('/admin-view', { state: item })}
                style={{ padding: '10px', backgroundColor: '#555', color: '#fff', borderRadius: '5px' }}
              >
                View Full Application
              </button>
            </div>
          ))}
        </div>
      )}

      <button
        onClick={() => navigate('/admin-dashboard')}
        style={{ marginTop: '30px', padding: '10px 20px', backgroundColor: '#222', color: '#fff', borderRadius: '5px' }}
      >
        â¬… Back to Dashboard
      </button>
    </div>
  );
}

export default AdminUnverifiedApplications;
