import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { signOut } from 'firebase/auth';
import { auth } from '../firebase';

function AdminDashboard() {
  const navigate = useNavigate();
  const location = useLocation();

  const [admin, setAdmin] = useState(null);

  useEffect(() => {
    const stateAdmin = location.state?.staff;
    const storedAdmin = localStorage.getItem('admin');

    if (stateAdmin) {
      localStorage.setItem('admin', JSON.stringify(stateAdmin));
      setAdmin(stateAdmin);
    } else if (storedAdmin) {
      setAdmin(JSON.parse(storedAdmin));
    } else {
      navigate('/staff-login');
    }
  }, [location.state, navigate]);

  const logout = async () => {
    await signOut(auth);
    localStorage.removeItem('admin');
    navigate('/');
  };

  if (!admin) return null;

  return (
    <div style={{ padding: '20px' }}>
      <h1>Welcome, {admin.name} (Admin)</h1>
      <p><strong>Email:</strong> {admin.email}</p>
      <p><strong>Phone:</strong> {admin.phone}</p>

      <button
        style={{ marginTop: '20px', display: 'block' }}
        onClick={() => navigate('/admin-manage-staff', { state: { admin } })}
      >
        Manage Staff
      </button>

      <button
        style={{ marginTop: '20px', display: 'block' }}
        onClick={() => navigate('/admin-manage-applications', { state: { admin } })}
      >
        Manage Applications
      </button>

      <button
        style={{ marginTop: '20px', display: 'block' }}
        onClick={() => navigate('/admin-view-applicants', { state: { admin } })}
      >
        View Applicants
      </button>

      <button
        onClick={logout}
        style={{ marginTop: '20px', display: 'block' }}
      >
        Logout
      </button>
    </div>
  );
}

export default AdminDashboard;
