import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db } from '../firebase';
import { signOut } from 'firebase/auth';
import { collection, getDocs, query, orderBy } from 'firebase/firestore';

function Dashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [announcements, setAnnouncements] = useState([]);
  const [applications, setApplications] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredApps, setFilteredApps] = useState([]);

  // Redirect if not logged in
  useEffect(() => {
    const currentUser = auth.currentUser;
    if (currentUser) {
      setUser(currentUser);
    } else {
      navigate('/login');
    }
  }, [navigate]);

  // Fetch announcements
  useEffect(() => {
    const fetchAnnouncements = async () => {
      const q = query(collection(db, 'announcements'), orderBy('startDate', 'desc'));
      const snapshot = await getDocs(q);
      const ann = snapshot.docs.map(doc => doc.data());
      setAnnouncements(ann);
    };
    fetchAnnouncements();
  }, []);

  // Fetch applications with status "Live"
  useEffect(() => {
    const fetchApplications = async () => {
      const snapshot = await getDocs(collection(db, 'applications'));
      const liveApps = snapshot.docs
        .map(doc => ({ id: doc.id, ...doc.data() }))
        .filter(app => app.status === 'Live');
      setApplications(liveApps);
      setFilteredApps(liveApps); // show live apps by default
    };
    fetchApplications();
  }, []);

  const handleSearch = () => {
    const search = searchTerm.toLowerCase();
    const matches = applications.filter(app =>
      app.name.toLowerCase().includes(search)
    );
    setFilteredApps(matches);
  };

  const logout = async () => {
    await signOut(auth);
    alert('Logged out');
    navigate('/login');
  };

  const goToApply = (appId) => {
    navigate('/apply', { state: { user: user.email, applicationId: appId } });
  };

  const goToCheckStatus = (appId) => {
    navigate('/check-status', { state: { user: user.email, applicationId: appId } });
  };

  if (!user) return <p>Loading...</p>;

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: 'auto' }}>
      <h2>Welcome, {user.email}</h2>

      {/* Search bar */}
      <div style={{ marginBottom: '20px', display: 'flex', gap: '10px' }}>
        <input
          type="text"
          placeholder="Search applications..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{
            flex: 1,
            padding: '10px',
            border: '1px solid #ccc',
            borderRadius: '5px'
          }}
        />
        <button onClick={handleSearch} style={{ padding: '10px 20px' }}>
          Search
        </button>
      </div>

      {/* Applications List */}
      <h3>Available Applications</h3>
      {filteredApps.length === 0 ? (
        <p>No matching applications found.</p>
      ) : (
        filteredApps.map(app => (
          <div key={app.id} style={{
            background: '#f9f9f9',
            padding: '10px',
            borderRadius: '5px',
            marginBottom: '10px',
            border: '1px solid #ddd'
          }}>
            <p><strong>{app.name}</strong> - {app.category}</p>
            <p>{app.message}</p>
            <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
              <button onClick={() => goToApply(app.id)}>Apply</button>
              <button onClick={() => goToCheckStatus(app.id)}>Check Status</button>
            </div>
          </div>
        ))
      )}

      {/* Announcements */}
      <div style={{ marginTop: '30px' }}>
        <h3>Announcements</h3>
        {announcements.length === 0 ? (
          <p>No announcements available.</p>
        ) : (
          announcements.map((a, idx) => (
            <div key={idx} style={{
              background: '#f0f0f0',
              padding: '10px',
              borderRadius: '5px',
              marginBottom: '10px'
            }}>
              {a.message}
            </div>
          ))
        )}
      </div>

      {/* Bottom buttons */}
      <div style={{ marginTop: '30px' }}>
        <button
          onClick={() => navigate('/profile')}
          style={{
            padding: '10px 20px',
            backgroundColor: '#4caf50',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            marginRight: '10px'
          }}
        >
          Profile
        </button>

        <button
          onClick={logout}
          style={{
            padding: '10px 20px',
            backgroundColor: '#f44336',
            color: 'white',
            border: 'none',
            borderRadius: '4px'
          }}
        >
          Logout
        </button>
      </div>
    </div>
  );
}

export default Dashboard;
