import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db } from '../firebase';
import { signOut } from 'firebase/auth';
import { collection, getDocs, query, orderBy } from 'firebase/firestore';
import '../styles/Dashboard.css';

function Dashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [announcements, setAnnouncements] = useState([]);
  const [applications, setApplications] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredApps, setFilteredApps] = useState([]);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [openAnnounce, setOpenAnnounce] = useState(null);

  useEffect(() => {
    const currentUser = auth.currentUser;
    if (currentUser) {
      setUser(currentUser);
    } else {
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    const fetchAnnouncements = async () => {
      const q = query(collection(db, 'announcements'), orderBy('startDate', 'desc'));
      const snapshot = await getDocs(q);
      const ann = snapshot.docs.map(doc => doc.data());
      setAnnouncements(ann);
    };
    fetchAnnouncements();
  }, []);

  useEffect(() => {
    const fetchApplications = async () => {
      const snapshot = await getDocs(collection(db, 'applications'));
      const liveApps = snapshot.docs
        .map(doc => ({ id: doc.id, ...doc.data() }))
        .filter(app => app.status === 'Live');
      setApplications(liveApps);
      setFilteredApps(liveApps);
    };
    fetchApplications();
  }, []);

  const handleSearch = () => {
    const search = searchTerm.toLowerCase();
    const matches = applications.filter(app =>
      app.name.toLowerCase().includes(search)
    );
    setFilteredApps(matches);
  };

  const logout = async () => {
    await signOut(auth);
    alert('Logged out');
    navigate('/login');
  };

  const goToApply = (appId) => {
    navigate('/apply', { state: { user: user.email, applicationId: appId } });
  };

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  const handleAnnounceClick = (index) => {
    setOpenAnnounce(openAnnounce === index ? null : index);
  };

  if (!user) return <p>Loading...</p>;

  return (
    <div>
      {/* Side Drawer */}
      <div className={`side-drawer ${drawerOpen ? 'open' : ''}`}>
        <h3>Navigation</h3>
        <ul>
          <li onClick={() => navigate('/')}>Home</li>
          <li onClick={() => navigate('/apply')}>Apply New</li>
          <li onClick={() => navigate('/check-status')}>Check Status</li>
          <li onClick={() => navigate('/profile')}>Profile</li>
          <li onClick={() => navigate('/about')}>About</li>
        </ul>
      </div>

      {/* Top Navbar */}
      <div className="navbar">
        <div className="left">
          <div className="logo" onClick={toggleDrawer}>Epanchayath 360</div>
        </div>
        <div className="right">
          <button onClick={() => navigate('/check-status')}>
            <span className="hamburger">&#9776;</span> Check Status
          </button>
          <button onClick={() => navigate('/about')}>About</button>
          <button onClick={() => navigate('/profile')}>Profile</button>
        </div>
      </div>

      {/* Body */}
      <div className="dashboard-container">
        <h2>Welcome, {user.email}</h2>

        {/* Search */}
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search applications..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button onClick={handleSearch}>Search</button>
        </div>

        {/* Applications */}
        <h3>Available Applications</h3>
        {filteredApps.length === 0 ? (
          <p>No matching applications found.</p>
        ) : (
          filteredApps.map(app => (
            <div key={app.id} className="app-card">
              <p><strong>{app.name}</strong> - {app.category}</p>
              <p>{app.message}</p>
              <button onClick={() => goToApply(app.id)}>Apply</button>
            </div>
          ))
        )}

        {/* Announcements */}
        <div className="announcements">
          <h3>Announcements</h3>
          {announcements.length === 0 ? (
            <p>No announcements available.</p>
          ) : (
            announcements.map((a, idx) => (
              <div
                key={idx}
                className={`announcement ${openAnnounce === idx ? 'open' : ''}`}
                onClick={() => handleAnnounceClick(idx)}
              >
                {a.message}
              </div>
            ))
          )}
        </div>

        {/* Bottom Buttons */}
        <div className="footer-buttons">
          <button className="profile" onClick={() => navigate('/profile')}>Profile</button>
          <button className="status" onClick={() => navigate('/check-status')}>Check Status</button>
          <button className="logout" onClick={logout}>Logout</button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
