import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db } from '../firebase';
import { signOut } from 'firebase/auth';
import { collection, getDocs, query, orderBy } from 'firebase/firestore';
import '../styles/Dashboard.css';

function Dashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [announcements, setAnnouncements] = useState([]);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [openAnnounce, setOpenAnnounce] = useState(null);

  useEffect(() => {
    const currentUser = auth.currentUser;
    if (currentUser) {
      setUser(currentUser);
    } else {
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    const fetchAnnouncements = async () => {
      const q = query(collection(db, 'announcements'), orderBy('startDate', 'desc'));
      const snapshot = await getDocs(q);
      const ann = snapshot.docs.map(doc => doc.data());
      setAnnouncements(ann);
    };
    fetchAnnouncements();
  }, []);

  const logout = async () => {
    await signOut(auth);
    alert('Logged out');
    navigate('/login');
  };

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  const handleAnnounceClick = (index) => {
    setOpenAnnounce(openAnnounce === index ? null : index);
  };

  if (!user) return <p>Loading...</p>;

  return (
    <div>
      {/* Side Drawer */}
      <div className={`side-drawer ${drawerOpen ? 'open' : ''}`}>
        <h3>Navigation</h3>
        <ul>
          <li onClick={() => navigate('/')}>Home</li>
          <li onClick={() => navigate('/apply')}>Apply New</li>
          <li onClick={() => navigate('/profile')}>Profile</li>
          <li onClick={() => navigate('/about')}>About</li>
        </ul>
      </div>

      {/* Top Navbar */}
      <div className="navbar">
        <div className="left">
          <div className="logo" onClick={toggleDrawer}>Epanchayath 360</div>
        </div>
        <div className="right">
          <button onClick={() => navigate('/about')}>About</button>
          <button onClick={() => navigate('/profile')}>Profile</button>
        </div>
      </div>

      {/* Main Content */}
      <div className="dashboard-container">
        <h2>Welcome, {user.email}</h2>

        {/* Announcements */}
        <div className="announcements">
          <h3>Announcements</h3>
          {announcements.length === 0 ? (
            <p>No announcements available.</p>
          ) : (
            announcements.map((a, idx) => (
              <div
                key={idx}
                className={`announcement ${openAnnounce === idx ? 'open' : ''}`}
                onClick={() => handleAnnounceClick(idx)}
              >
                {a.message}
              </div>
            ))
          )}
        </div>

        {/* Footer */}
        <div className="footer-buttons">
          <button className="logout" onClick={logout}>Logout</button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
