import React, { useEffect, useState } from 'react';
import { collection, getDocs, query, where } from 'firebase/firestore';
import { db } from '../firebase'; // Your Firebase config

function ViewStatus({ userId }) {
  const [application, setApplication] = useState(null);
  const [status, setStatus] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStatus = async () => {
      try {
        // Get Application from 'Applied' using userId
        const appliedQuery = query(collection(db, 'Applied'), where('userId', '==', userId));
        const appliedSnap = await getDocs(appliedQuery);

        if (!appliedSnap.empty) {
          const appDoc = appliedSnap.docs[0];
          const appData = appDoc.data();
          setApplication(appData);

          // Determine initial status
          let currentStatus = appData.verify ? 'Verified' : 'Applied';

          // Check 'CheckedApp' collection
          const checkedQuery = query(collection(db, 'CheckedApp'), where('appliedId', '==', appDoc.id));
          const checkedSnap = await getDocs(checkedQuery);

          if (!checkedSnap.empty) {
            const checkedData = checkedSnap.docs[0].data();
            if (checkedData.status) {
              currentStatus = checkedData.status;
            }
          }

          setStatus(currentStatus);
        } else {
          setStatus('No application found.');
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching application:', error);
        setStatus('Error fetching application.');
        setLoading(false);
      }
    };

    fetchStatus();
  }, [userId]);

  if (loading) return <p>Loading...</p>;
  if (!application) return <p>{status}</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Application Status</h2>
      <p><strong>Name:</strong> {application.name}</p>
      <p><strong>Occupation:</strong> {application.occupation}</p>
      <p><strong>Phone:</strong> {application.phone}</p>
      <p><strong>Ward:</strong> {application.ward}</p>
      <p><strong>Status:</strong> {status}</p>
    </div>
  );
}

export default ViewStatus;
