import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import {
  collection,
  getDocs,
  query,
  where,
  doc,
  getDoc
} from 'firebase/firestore';
import { db } from '../firebase';

function StaffDashboard() {
  const location = useLocation();
  const navigate = useNavigate();
  const staff = location.state?.staff;

  const [category, setCategory] = useState('');
  const [appliedData, setAppliedData] = useState([]);

  useEffect(() => {
    if (!staff) {
      navigate('/staff-login');
    }
  }, [staff, navigate]);

  const handleCategoryChange = async (e) => {
    const selectedCategory = e.target.value;
    setCategory(selectedCategory);

    try {
      // Get all applications in that category
      const appSnap = await getDocs(
        query(collection(db, 'applications'), where('category', '==', selectedCategory))
      );

      const applicationsMap = {};
      appSnap.forEach(doc => {
        applicationsMap[doc.id] = doc.data();
      });

      // Get all Applied
      const appliedSnap = await getDocs(collection(db, 'Applied'));

      const matched = [];
      for (const docSnap of appliedSnap.docs) {
        const applied = docSnap.data();
        const app = applicationsMap[applied.applicationId];
        if (app) {
          matched.push({
            id: docSnap.id,
            applied,
            application: app
          });
        }
      }

      setAppliedData(matched);
    } catch (err) {
      console.error(err);
      alert('Error fetching applications.');
    }
  };

  if (!staff) return null;

  return (
    <div style={{ padding: '20px', maxWidth: '900px', margin: 'auto' }}>
      <h2>Welcome, {staff.name}</h2>
      <p><strong>Email:</strong> {staff.email}</p>
      <p><strong>Phone:</strong> {staff.phone}</p>

      <div style={{ marginTop: '20px' }}>
        <label>Select Category:</label>
        <select value={category} onChange={handleCategoryChange}>
          <option value="">-- Select --</option>
          <option value="Housing">Housing</option>
          <option value="Scholarship">Scholarship</option>
          <option value="Pension">Pension</option>
          <option value="Subsidy">Subsidy</option>
          <option value="Other">Other</option>
        </select>
      </div>

      {appliedData.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h3>Applications in {category}</h3>
          {appliedData.map((item, index) => (
            <div key={index} style={{ border: '1px solid #ccc', padding: '15px', marginBottom: '10px' }}>
              <p><strong>Applicant ID:</strong> {item.applied.userId}</p>
              <p><strong>Application:</strong> {item.application.name}</p>
              <p><strong>Message:</strong> {item.applied.message}</p>
              <button onClick={() =>
                navigate('/viewapp', {
                  state: {
                    applied: item.applied,
                    application: item.application,
                    appliedId: item.id
                  }
                })
              }>
                View
              </button>
            </div>
          ))}
        </div>
      )}

      <button onClick={() => navigate('/')} style={{ marginTop: '40px' }}>
        Logout
      </button>
    </div>
  );
}

export default StaffDashboard;
