import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { auth, db } from '../firebase';
import { signOut } from 'firebase/auth';
import { collection, getDocs, query, orderBy } from 'firebase/firestore';

function Dashboard() {
  const navigate = useNavigate();
  const location = useLocation();
  const user = location.state?.user || auth.currentUser?.email || 'User';

  const [searchTerm, setSearchTerm] = useState('');
  const [applications, setApplications] = useState([]);
  const [announcements, setAnnouncements] = useState([]);

  // Fetch applications
  useEffect(() => {
    const fetchApps = async () => {
      const snapshot = await getDocs(collection(db, 'applications'));
      const apps = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setApplications(apps);
    };
    fetchApps();
  }, []);

  // Fetch announcements
  useEffect(() => {
    const fetchAnnouncements = async () => {
      const q = query(collection(db, 'announcements'), orderBy('startDate', 'desc'));
      const snapshot = await getDocs(q);
      const anns = snapshot.docs.map(doc => doc.data());
      setAnnouncements(anns);
    };
    fetchAnnouncements();
  }, []);

  const logout = async () => {
    await signOut(auth);
    alert("Logged out");
    navigate('/login');
  };

  const filteredApps = applications.filter(app =>
    app.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: 'auto' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Welcome, {user}</h2>
        <button onClick={() => navigate('/profile')}>Profile</button>
      </div>

      <input
        type="text"
        placeholder="Search applications..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{
          width: '100%',
          padding: '10px',
          marginTop: '20px',
          marginBottom: '20px',
          fontSize: '16px'
        }}
      />

      <h3>Available Applications</h3>
      {filteredApps.length === 0 ? (
        <p>No applications found.</p>
      ) : (
        <ul>
          {filteredApps.map((app) => (
            <li key={app.id}>
              <strong>{app.name}</strong> - {app.category}
            </li>
          ))}
        </ul>
      )}

      <hr style={{ margin: '30px 0' }} />

      <h3>Announcements</h3>
      {announcements.length === 0 ? (
        <p>No announcements.</p>
      ) : (
        <ul>
          {announcements.map((a, index) => (
            <li key={index} style={{ marginBottom: '10px' }}>
              ðŸ“¢ {a.message}
            </li>
          ))}
        </ul>
      )}

      <button onClick={logout} style={{ marginTop: '40px' }}>
        Logout
      </button>
    </div>
  );
}

export default Dashboard;
