import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import {
  collection,
  getDocs,
  query,
  where
} from 'firebase/firestore';
import { db } from '../firebase';

function NewApplied() {
  const location = useLocation();
  const navigate = useNavigate();
  const staff = location.state?.staff;

  const [category, setCategory] = useState('');
  const [appliedData, setAppliedData] = useState([]);

  const predefinedCategories = [
    'Complaints',
    'Certificate',
    'Financial Support',
    'Customer Grievance',
    'Water Connection',
    'Electricity',
    'Housing Scheme'
  ];

  useEffect(() => {
    if (!staff) {
      navigate('/staff-login');
    }
  }, [staff, navigate]);

  // Fetch applications only when button clicked
  const fetchApplications = async () => {
    if (!category) {
      alert('Please select a category first.');
      return;
    }

    try {
      const appSnap = await getDocs(
        query(collection(db, 'applications'), where('category', '==', category))
      );

      const applicationsMap = {};
      appSnap.forEach(doc => {
        applicationsMap[doc.id] = doc.data();
      });

      const appliedSnap = await getDocs(collection(db, 'Applied'));

      const matched = [];
      for (const docSnap of appliedSnap.docs) {
        const applied = docSnap.data();
        const app = applicationsMap[applied.applicationId];

        if (app && applied.verify !== true) {
          matched.push({
            id: docSnap.id,
            applied,
            application: app
          });
        }
      }

      setAppliedData(matched);
    } catch (err) {
      console.error(err);
      alert('Error fetching applications.');
    }
  };

  if (!staff) return null;

  return (
    <div style={{ padding: '20px', maxWidth: '900px', margin: 'auto' }}>

      <div style={{ marginTop: '20px' }}>
        <label>Select Category: </label>
        <select value={category} onChange={e => setCategory(e.target.value)}>
          <option value="">-- Select Category --</option>
          {predefinedCategories.map((cat, idx) => (
            <option key={idx} value={cat}>{cat}</option>
          ))}
        </select>

        <button onClick={fetchApplications} style={{ marginLeft: '15px' }}>
          View Applications
        </button>
      </div>

      {appliedData.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h3>Applications in "{category}"</h3>
          {appliedData.map((item, index) => (
            <div key={index} style={{ border: '1px solid #ccc', padding: '15px', marginBottom: '10px' }}>
              <p><strong>Applicant ID:</strong> {item.applied.userId}</p>
              <p><strong>Application:</strong> {item.application.name}</p>
              <p><strong>Message:</strong> {item.applied.message}</p>
              <button onClick={() =>
                navigate('/viewapp', {
                  state: {
                    applied: item.applied,
                    application: item.application,
                    appliedId: item.id,
                    staffId: staff.id
                  }
                })
              }>
                View
              </button>
            </div>
          ))}
        </div>
      )}

      <button onClick={() => navigate('/')} style={{ marginTop: '40px' }}>
        Logout
      </button>
    </div>
  );
}

export default NewApplied;
