import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css';

function Home() {
  const [role, setRole] = useState('user');
  const navigate = useNavigate();

  const handleToggle = () => {
    setRole((prev) => (prev === 'user' ? 'staff' : 'user'));
  };

  const handleContinue = () => {
    navigate(role === 'user' ? '/login' : '/staff-login');
  };

  return (
    <div className="home-wrapper">
  <div className="left-container">
    <h2 className="welcome-text">Welcome to E-Grama Panchayath</h2>
    <p className="sub-text">Digitally connecting you with your local governance.</p>
    {/* You can also insert an image here */}
    {/* <img src="/assets/your-image.png" alt="Village illustration" className="welcome-image" /> */}
  </div>

  <div className="right-container">
    <h1 className="title">E-Grama Panchayath</h1>

    <div className="toggle-container">
      <label className="role-switch">
        <input
          type="checkbox"
          checked={role === 'staff'}
          onChange={handleToggle}
          aria-label="Toggle role between User and Staff"
        />
        <span
          className="slider-knob"
          data-role={role === 'user' ? 'User' : 'Staff'}
        ></span>
      </label>
    </div>

    <button
      className="continue-btn"
      onClick={handleContinue}
      aria-label="Continue to next step"
    >
      Continue
    </button>
  </div>
</div>
  );
}

export default Home;
