import React, { useEffect, useState } from 'react';
import {
  collection,
  getDocs,
  addDoc,
  updateDoc,
  deleteDoc,
  doc
} from 'firebase/firestore';
import { db, auth } from '../firebase';
import { useNavigate, useLocation } from 'react-router-dom';
import { signOut } from 'firebase/auth';

const predefinedCategories = [
  'Complaints',
  'Certificate',
  'Financial Support',
  'Customer Grievance',
  'Water Connection',
  'Electricity',
  'Housing Scheme'
];

function AdminAppliCap() {
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [drawerOpen, setDrawerOpen] = useState(true);

  const navigate = useNavigate();
  const location = useLocation();
  const admin = location.state?.admin;

  useEffect(() => {
    if (!admin) {
      alert('Access denied. Please login as admin.');
      navigate('/staff-login');
    }
  }, [admin, navigate]);

  const applicationsRef = collection(db, 'applications');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const snapshot = await getDocs(applicationsRef);
        const apps = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setApplications(apps);
      } catch (error) {
        console.error('Failed to fetch applications:', error);
      } finally {
        setLoading(false);
      }
    };
    if (admin) fetchData();
  }, [admin]);

  const updateLocalApplication = (index, key, value) => {
    const updated = [...applications];
    updated[index][key] = value;
    setApplications(updated);
  };

  const addApplication = async () => {
    const newApp = {
      name: '',
      message: '',
      category: predefinedCategories[0],
      status: 'Offline'
    };
    try {
      const docRef = await addDoc(applicationsRef, newApp);
      setApplications([...applications, { id: docRef.id, ...newApp }]);
    } catch (error) {
      alert('Error adding application: ' + error.message);
    }
  };

  const saveApplication = async (index) => {
    const app = applications[index];
    try {
      const appRef = doc(db, 'applications', app.id);
      await updateDoc(appRef, {
        name: app.name,
        message: app.message,
        category: app.category,
        status: app.status
      });
      alert('Application updated successfully.');
    } catch (error) {
      alert('Error updating application: ' + error.message);
    }
  };

  const deleteApplication = async (index) => {
    if (!window.confirm('Are you sure you want to delete this application?')) return;
    const app = applications[index];
    try {
      const appRef = doc(db, 'applications', app.id);
      await deleteDoc(appRef);
      setApplications(applications.filter((_, i) => i !== index));
    } catch (error) {
      alert('Error deleting application: ' + error.message);
    }
  };

  const logout = async () => {
    await signOut(auth);
    navigate('/');
  };

  if (!admin) return null;
  if (loading) return <p>Loading...</p>;

  return (
    <div style={{ display: 'flex', minHeight: '100vh' }}>
      {/* Sidebar */}
      <div
        className={`side-drawer ${drawerOpen ? 'open' : ''}`}
        style={{
          width: drawerOpen ? 220 : 0,
          transition: 'width 0.3s ease',
          backgroundColor: '#2c3e50',
          color: 'white',
          paddingTop: '20px',
          overflow: 'hidden'
        }}
      >
        <h3 style={{ paddingLeft: '20px' }}>Navigation</h3>
        <ul style={{ listStyle: 'none', padding: 0 }}>
          <li
            style={{ padding: '12px 20px', cursor: 'pointer' }}
            onClick={() => navigate('/admin-dashboard', { state: { admin } })}
          >
            Home
          </li>
          <li
            style={{ padding: '12px 20px', cursor: 'pointer' }}
            onClick={() => navigate('/admin-manage-staff', { state: { admin } })}
          >
            Manage Staff
          </li>
          <li
            style={{ padding: '12px 20px', cursor: 'pointer' }}
            onClick={() => navigate('/admin-manage-applications', { state: { admin } })}
          >
            Manage Applications
          </li>
          <li
            style={{ padding: '12px 20px', cursor: 'pointer' }}
            onClick={() => navigate('/admin-view-applicants', { state: { admin } })}
          >
            View Applicants
          </li>
          <li
            style={{ padding: '12px 20px', cursor: 'pointer' }}
            onClick={() => navigate('/admin-manage-announcement', { state: { admin } })}
          >
            Manage Announcements
          </li>
          <li
            style={{ padding: '12px 20px', cursor: 'pointer' }}
            onClick={() => navigate('/admin-monitor-status', { state: { admin } })}
          >
            Monitor Application Status
          </li>
          <li
            style={{ padding: '12px 20px', cursor: 'pointer', marginTop: '30px', backgroundColor: '#c0392b' }}
            onClick={logout}
          >
            Logout
          </li>
        </ul>
      </div>

      <div style={{ flexGrow: 1 }}>
        {/* Navbar */}
        <nav
          style={{
            height: '60px',
            backgroundColor: '#34495e',
            color: 'white',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'space-between',
            padding: '0 20px'
          }}
        >
          <button
            onClick={() => setDrawerOpen(!drawerOpen)}
            style={{
              fontSize: '24px',
              background: 'none',
              border: 'none',
              color: 'white',
              cursor: 'pointer'
            }}
            aria-label="Toggle Sidebar"
          >
            â˜°
          </button>
          <div>
            Welcome, <strong>{admin.name}</strong> (Admin)
          </div>
          <button
            onClick={logout}
            style={{
              backgroundColor: '#c0392b',
              border: 'none',
              padding: '8px 16px',
              color: 'white',
              cursor: 'pointer',
              borderRadius: '4px'
            }}
          >
            Logout
          </button>
        </nav>

        {/* Main Content */}
        <div style={{ padding: '20px' }}>
          <h2>Manage Applications</h2>
          <table
            border="1"
            cellPadding="10"
            cellSpacing="0"
            style={{ width: '100%', marginTop: '20px' }}
          >
            <thead>
              <tr>
                <th>Application Name</th>
                <th>Message</th>
                <th>Category</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {applications.map((app, index) => (
                <tr key={app.id}>
                  <td>
                    <input
                      type="text"
                      value={app.name}
                      onChange={(e) => updateLocalApplication(index, 'name', e.target.value)}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={app.message}
                      onChange={(e) => updateLocalApplication(index, 'message', e.target.value)}
                    />
                  </td>
                  <td>
                    <select
                      value={app.category}
                      onChange={(e) => updateLocalApplication(index, 'category', e.target.value)}
                    >
                      {predefinedCategories.map((cat, i) => (
                        <option key={i} value={cat}>
                          {cat}
                        </option>
                      ))}
                    </select>
                  </td>
                  <td>
                    <select
                      value={app.status}
                      onChange={(e) => updateLocalApplication(index, 'status', e.target.value)}
                    >
                      <option value="Live">Live</option>
                      <option value="Offline">Offline</option>
                    </select>
                  </td>
                  <td>
                    <button onClick={() => saveApplication(index)}>Update</button>
                    <button onClick={() => deleteApplication(index)} style={{ marginLeft: '10px' }}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <button onClick={addApplication} style={{ marginTop: '20px' }}>
            Add New Application
          </button>

          <button
            style={{ marginTop: '20px', display: 'block' }}
            onClick={() => navigate('/admin-dashboard', { state: { admin } })}
          >
            Back to Dashboard
          </button>
        </div>
      </div>
    </div>
  );
}

export default AdminAppliCap;
