import React, { useState } from 'react';

const predefinedCategories = [
  'Complaints',
  'Certificate',
  'Financial Support',
  'Customer Grievance',
  'Water Connection',
  'Electricity',
  'Housing Scheme'
];

function AdminAppliCap() {
  const [applications, setApplications] = useState([
    {
      name: 'Water Issue Complaint',
      message: 'Residents facing water shortage',
      category: 'Complaints',
      status: 'Live'
    },
    {
      name: 'Birth Certificate Request',
      message: 'Application for new birth certificate',
      category: 'Certificate',
      status: 'Offline'
    }
  ]);

  const updateApplication = (index, key, value) => {
    const updated = [...applications];
    updated[index][key] = value;
    setApplications(updated);
  };

  const addApplication = () => {
    setApplications([
      ...applications,
      {
        name: '',
        message: '',
        category: predefinedCategories[0],
        status: 'Offline'
      }
    ]);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Manage Applications</h2>
      <table border="1" cellPadding="10" cellSpacing="0" style={{ width: '100%', marginTop: '20px' }}>
        <thead>
          <tr>
            <th>Application Name</th>
            <th>Message</th>
            <th>Category</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {applications.map((app, index) => (
            <tr key={index}>
              <td>
                <input
                  type="text"
                  value={app.name}
                  onChange={(e) => updateApplication(index, 'name', e.target.value)}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={app.message}
                  onChange={(e) => updateApplication(index, 'message', e.target.value)}
                />
              </td>
              <td>
                <select
                  value={app.category}
                  onChange={(e) => updateApplication(index, 'category', e.target.value)}
                >
                  {predefinedCategories.map((cat, i) => (
                    <option key={i} value={cat}>
                      {cat}
                    </option>
                  ))}
                </select>
              </td>
              <td>
                <select
                  value={app.status}
                  onChange={(e) => updateApplication(index, 'status', e.target.value)}
                >
                  <option value="Live">Live</option>
                  <option value="Offline">Offline</option>
                </select>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={addApplication} style={{ marginTop: '20px' }}>
        Add New Application
      </button>
    </div>
  );
}

export default AdminAppliCap;
