import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { auth, db } from '../firebase';
import { signOut, createUserWithEmailAndPassword } from 'firebase/auth';
import {
  collection, getDocs, setDoc, doc, deleteDoc, updateDoc
} from 'firebase/firestore';

function AdminDashboard() {
  const navigate = useNavigate();
  const location = useLocation();
  const admin = location.state?.staff;

  const [staffList, setStaffList] = useState([]);
  const [newStaff, setNewStaff] = useState({
    staffId: '',
    name: '',
    email: '',
    phone: '',
    password: '',
    salary: ''
  });

  const fetchStaff = async () => {
    const snapshot = await getDocs(collection(db, 'staff'));
    const staffArray = snapshot.docs.map(doc => ({ ...doc.data(), id: doc.id }));
    setStaffList(staffArray);
  };

  useEffect(() => {
    if (!admin) {
      navigate('/staff-login');
    } else {
      fetchStaff();
    }
  }, []);

  const logout = async () => {
    await signOut(auth);
    navigate('/');
  };

  const handleAddStaff = async (e) => {
    e.preventDefault();
    try {
      const userCred = await createUserWithEmailAndPassword(auth, newStaff.email, newStaff.password);
      const uid = userCred.user.uid;
      await setDoc(doc(db, 'staff', newStaff.staffId), {
        ...newStaff,
        uid
      });
      alert('Staff added!');
      setNewStaff({ staffId: '', name: '', email: '', phone: '', password: '', salary: '' });
      fetchStaff();
    } catch (error) {
      alert('Error adding staff: ' + error.message);
    }
  };

  const updateSalary = async (id, salary) => {
    try {
      await updateDoc(doc(db, 'staff', id), { salary });
      fetchStaff();
    } catch (error) {
      alert('Error updating salary: ' + error.message);
    }
  };

  const deleteStaff = async (id) => {
    if (!window.confirm('Are you sure you want to delete this staff member?')) return;
    try {
      await deleteDoc(doc(db, 'staff', id));
      fetchStaff();
    } catch (error) {
      alert('Error deleting staff: ' + error.message);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Welcome, {admin?.name} (Admin)</h1>
      <p><strong>Email:</strong> {admin?.email}</p>

      <h2>Add New Staff</h2>
      <form onSubmit={handleAddStaff} style={{ display: 'flex', flexDirection: 'column', maxWidth: '300px' }}>
        {['staffId', 'name', 'email', 'phone', 'password', 'salary'].map(field => (
          <input
            key={field}
            type={field === 'password' ? 'password' : 'text'}
            placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
            value={newStaff[field]}
            required
            onChange={(e) => setNewStaff({ ...newStaff, [field]: e.target.value })}
          />
        ))}
        <button type="submit">Add Staff</button>
      </form>

      <h2>All Staff</h2>
      <table border="1" cellPadding="10" style={{ marginTop: '20px' }}>
        <thead>
          <tr>
            <th>ID</th><th>Name</th><th>Email</th><th>Phone</th><th>Salary</th><th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {staffList.map((staff) => (
            <tr key={staff.staffId}>
              <td>{staff.staffId}</td>
              <td>{staff.name}</td>
              <td>{staff.email}</td>
              <td>{staff.phone}</td>
              <td>
                <input
                  type="text"
                  defaultValue={staff.salary}
                  onBlur={(e) => updateSalary(staff.staffId, e.target.value)}
                />
              </td>
              <td>
                <button onClick={() => deleteStaff(staff.staffId)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={logout} style={{ marginTop: '20px' }}>Logout</button>
    </div>
  );
}

export default AdminDashboard;
