import React, { useEffect, useState } from 'react';
import { collection, getDocs } from 'firebase/firestore';
import { db } from '../firebase';
import { useNavigate } from 'react-router-dom';
import { signOut } from 'firebase/auth';
import { auth } from '../firebase';

function AdminUnverifiedApplications() {
  const [unverified, setUnverified] = useState([]);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [admin, setAdmin] = useState(null);
  const navigate = useNavigate();

  // Load admin info from localStorage or redirect if not logged in
  useEffect(() => {
    const storedAdmin = localStorage.getItem('admin');
    if (storedAdmin) {
      setAdmin(JSON.parse(storedAdmin));
    } else {
      navigate('/staff-login');
    }
  }, [navigate]);

  const logout = async () => {
    await signOut(auth);
    localStorage.removeItem('admin');
    navigate('/');
  };

  const toggleDrawer = () => setDrawerOpen(!drawerOpen);

  useEffect(() => {
    if (!admin) return;

    const fetchUnverified = async () => {
      const appliedSnapshot = await getDocs(collection(db, 'Applied'));
      const apps = [];

      // Fetch all applications to match IDs for better performance 
      const appSnapshot = await getDocs(collection(db, 'applications'));
      const appMap = {};
      appSnapshot.forEach(doc => {
        appMap[doc.id] = doc.data();
      });

      for (const docSnap of appliedSnapshot.docs) {
        const appliedData = docSnap.data();
        const appliedId = docSnap.id;

        if (appliedData.verify === 'false') {
          const applicationData = appMap[appliedData.applicationId] || null;

          apps.push({
            appliedId,
            applied: appliedData,
            application: applicationData,
            staffId: admin.id || 'admin',
          });
        }
      }

      setUnverified(apps);
    };

    fetchUnverified();
  }, [admin]);

  if (!admin) return null;

  return (
    <div>
      {/* Sidebar */}
      <div className={`side-drawer ${drawerOpen ? 'open' : ''}`}>
        <h3>Navigation</h3>
        <ul>
          <li onClick={() => navigate('/admin-dashboard', { state: { admin } })}>Home</li>
          <li onClick={() => navigate('/admin-manage-staff', { state: { admin } })}>Manage Staff</li>
          <li onClick={() => navigate('/admin-manage-applications', { state: { admin } })}>Manage Applications</li>
          <li onClick={() => navigate('/admin-view-applicants', { state: { admin } })}>View Applicants</li>
          <li onClick={() => navigate('/admin-manage-announcement', { state: { admin } })}>Manage Announcements</li>
          <li onClick={() => navigate('/admin-monitor-status', { state: { admin } })}>Monitor Application Status</li>
          <li className="logout" onClick={logout}>Logout</li>
        </ul>
      </div>

      {/* Navbar */}
      <div className="navbar">
        <div className="left" onClick={toggleDrawer} style={{ cursor: 'pointer' }}>
          <span className="hamburger">&#9776;</span>
          <span className="logo">E-panchayat 360</span>
        </div>
        <div className="right">
          <button onClick={() => navigate('/About-')}>About</button>
          <button onClick={() => navigate('/admin-profile', { state: { admin } })}>Profile</button>
        </div>
      </div>

      {/* Main content container, centered */}
      <div
        style={{
          padding: '20px',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'flex-start',
          minHeight: 'calc(100vh - 60px)',
          boxSizing: 'border-box',
        }}
      >
        <div style={{ width: '700px' }}>
          <h2>Unverified Applications</h2>

          {unverified.length === 0 ? (
            <p>No unverified applications found.</p>
          ) : (
            <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
              {unverified.map((item, index) => (
                <div
                  key={index}
                  style={{
                    padding: '15px',
                    border: '1px solid #ccc',
                    borderRadius: '10px',
                  }}
                >
                  <p><strong>Applicant Name:</strong> {item.applied.name}</p>
                  <p><strong>Phone:</strong> {item.applied.phone}</p>
                  <p><strong>Occupation:</strong> {item.applied.occupation}</p>
                  <p><strong>Application ID:</strong> {item.appliedId}</p>
                  <button
                    onClick={() =>
                      navigate('/admin-view', { state: { ...item, admin } })
                    }
                    style={{
                      padding: '10px',
                      backgroundColor: '#555',
                      color: '#fff',
                      borderRadius: '5px',
                      cursor: 'pointer',
                    }}
                  >
                    View Full Application
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default AdminUnverifiedApplications;
