import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { signOut } from 'firebase/auth';
import { auth } from '../firebase';

function AdminDashboard() {
  const navigate = useNavigate();
  const location = useLocation();

  const [admin, setAdmin] = useState(null);
  const [drawerOpen, setDrawerOpen] = useState(false);

  useEffect(() => {
    const stateAdmin = location.state?.staff;
    const storedAdmin = localStorage.getItem('admin');

    if (stateAdmin) {
      localStorage.setItem('admin', JSON.stringify(stateAdmin));
      setAdmin(stateAdmin);
    } else if (storedAdmin) {
      setAdmin(JSON.parse(storedAdmin));
    } else {
      navigate('/staff-login');
    }
  }, [location.state, navigate]);

  const logout = async () => {
    await signOut(auth);
    localStorage.removeItem('admin');
    navigate('/');
  };

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  if (!admin) return null;

  return (
    <div>
      {/* Sidebar */}
      <div className={`side-drawer ${drawerOpen ? 'open' : ''}`}>
        <h3>Navigation</h3>
        <ul>
          <li onClick={() => navigate('/admin-dashboard', { state: { staff: admin } })}>
            Home
          </li>
          <li onClick={() => navigate('/admin-manage-staff', { state: { staff: admin } })}>
            Manage Staff
          </li>
          <li onClick={() => navigate('/admin-manage-applications', { state: { staff: admin } })}>
            Manage Applications
          </li>
          <li onClick={() => navigate('/admin-view-applicants', { state: { staff: admin } })}>
            View Applicants
          </li>
          <li onClick={() => navigate('/admin-manage-announcement', { state: { staff: admin } })}>
            Manage Announcements
          </li>
          <li onClick={() => navigate('/admin-monitor-status', { state: { staff: admin } })}>
            Monitor Application Status
          </li>
          <li className="logout" onClick={logout}>
            Logout
          </li>
        </ul>
      </div>

      {/* Navbar */}
      <div className="navbar">
        <div className="left" onClick={toggleDrawer} style={{ cursor: 'pointer' }}>
          <span className="hamburger">&#9776;</span>
          <span className="logo">Epanchayath 360</span>
        </div>
        <div className="right">
          <button onClick={() => navigate('/About-')}>About</button>
          <button onClick={() => navigate('/admin-profile', { state: { staff: admin } })}>
            Profile
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="dashboard-container" style={{ padding: '20px' }}>
        <h1>Welcome, {admin.name} (Admin)</h1>
        <p><strong>Email:</strong> {admin.email}</p>
        <p><strong>Phone:</strong> {admin.phone}</p>
      </div>
    </div>
  );
}

export default AdminDashboard;
