// src/components/StaffLogin.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { doc, getDoc } from 'firebase/firestore';
import { auth, db } from '../firebase';
import '../styles/form.css';

function StaffLogin() {
  const [staffId, setStaffId] = useState('');
  const [password, setPassword] = useState('');
  const [isAdmin, setIsAdmin] = useState(false); // ✅ NEW toggle
  const navigate = useNavigate();

  const login = async (e) => {
    e.preventDefault();

    const collectionName = isAdmin ? 'admin' : 'staff'; // ✅ switch between 'staff' and 'admin'

    try {
      const userRef = doc(db, collectionName, staffId);
      const userSnap = await getDoc(userRef);

      if (!userSnap.exists()) {
        alert(`${isAdmin ? 'Admin' : 'Staff'} ID not found`);
        return;
      }

      const user = userSnap.data();
      await signInWithEmailAndPassword(auth, user.email, password);

      const route = isAdmin ? '/admin-dashboard' : '/staff-dashboard';
      navigate(route, { state: { staff: user } });

    } catch (err) {
      alert('Login failed: ' + err.message);
    }
  };

  return (
  <div className="toggle-wrapper">
    <label className="toggle-label">
      <span>Staff</span>
      <input
        type="checkbox"
        checked={isAdmin}
        onChange={() => setIsAdmin(!isAdmin)}
      />
      <span className="toggle-slider" />
      <span>Admin</span>
    </label>

    <form onSubmit={login}>
      <h2>{isAdmin ? 'Admin Login' : 'Staff Login'}</h2>
      <input
        type="text"
        placeholder={isAdmin ? "Admin ID" : "Staff ID"}
        required
        onChange={(e) => setStaffId(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        required
        onChange={(e) => setPassword(e.target.value)}
      />
      <button type="submit">Login</button>
      <button type="button" onClick={() => navigate('/')}>Back to Home</button>
    </form>
  </div>
);
}

export default StaffLogin;
