// src/components/StaffLogin.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { doc, getDoc } from 'firebase/firestore';
import { auth, db } from '../firebase';
import '../styles/form.css';

function StaffLogin() {
  const [staffId, setStaffId] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const login = async (e) => {
    e.preventDefault();

    try {
      // 1. Fetch staff details from Firestore
      const staffRef = doc(db, 'staff', staffId);
      const staffSnap = await getDoc(staffRef);

      if (!staffSnap.exists()) {
        alert('Staff ID not found');
        return;
      }

      const staff = staffSnap.data();

      // 2. Use stored email to log in via Firebase Auth
      const userCredential = await signInWithEmailAndPassword(auth, staff.email, password);

      // 3. Auth successful, navigate with staff details
      navigate('/staff-dashboard', { state: { staff } });

    } catch (err) {
      alert('Login failed: ' + err.message);
    }
  };

  return (
    <form onSubmit={login}>
      <h2>Staff Login</h2>
      <input
        type="text"
        placeholder="Staff ID"
        required
        onChange={(e) => setStaffId(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        required
        onChange={(e) => setPassword(e.target.value)}
      />
      <button type="submit">Login</button>
      <button type="button" onClick={() => navigate('/')}>Back to Home</button>
    </form>
  );
}

export default StaffLogin;
