import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/home.css';

function Home() {
  const [role, setRole] = useState('user');
  const navigate = useNavigate();

  const handleToggle = () => {
    setRole((prev) => (prev === 'user' ? 'staff' : 'user'));
  };

  const handleContinue = () => {
    if (role === 'user') {
      navigate('/login');
    } else {
      navigate('/staff-login');
    }
  };

  return (
    <div className="home-wrapper">
      <div className="right-container">
        <h1 className="title">E-Grama Panchayath</h1>

        {/* Labels above */}
        <div className="labels-above">
          <span className={role === 'user' ? 'label active' : 'label'}>User</span>
          <span className={role === 'staff' ? 'label active' : 'label'}>Staff</span>
        </div>

        {/* Toggle switch */}
        <div className="slider-toggle">
          <label className="switch">
            <input type="checkbox" checked={role === 'staff'} onChange={handleToggle} />
            <span className="slider"></span>
          </label>
        </div>

        <button className="continue-btn" onClick={handleContinue}>
          Continue
        </button>
      </div>
    </div>
  );
}

export default Home;
