import React, { useEffect, useState } from 'react';
import { collection, getDocs, updateDoc, doc } from 'firebase/firestore';
import { db } from '../firebase';
import { useNavigate, useLocation } from 'react-router-dom';

const statusOptions = ['Approved', 'Declined', 'Not Viewed'];

function AdminViewApplicants() {
  const navigate = useNavigate();
  const location = useLocation();

  const [admin, setAdmin] = useState(null);
  const [applicants, setApplicants] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Get admin info from location state or localStorage
    const stateAdmin = location.state?.admin;
    const storedAdmin = localStorage.getItem('admin');

    if (stateAdmin) {
      localStorage.setItem('admin', JSON.stringify(stateAdmin));
      setAdmin(stateAdmin);
    } else if (storedAdmin) {
      setAdmin(JSON.parse(storedAdmin));
    } else {
      navigate('/staff-login');
    }
  }, [location.state, navigate]);

  useEffect(() => {
    if (!admin) return;

    const fetchApplicants = async () => {
      setLoading(true);
      try {
        const applicantsRef = collection(db, 'applicants');
        const snapshot = await getDocs(applicantsRef);
        const apps = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setApplicants(apps);
      } catch (error) {
        console.error('Error fetching applicants:', error);
      }
      setLoading(false);
    };

    fetchApplicants();
  }, [admin]);

  const updateStatus = async (index) => {
    const applicant = applicants[index];
    if (!applicant.id) return;

    try {
      const applicantRef = doc(db, 'applicants', applicant.id);
      await updateDoc(applicantRef, { status: applicant.status });
      alert(`Status updated for applicant ${applicant.id}`);
    } catch (error) {
      console.error('Error updating status:', error);
      alert('Failed to update status');
    }
  };

  const updateLocalStatus = (index, newStatus) => {
    const updated = [...applicants];
    updated[index].status = newStatus;
    setApplicants(updated);
  };

  if (!admin) return null;
  if (loading) return <p>Loading applicants...</p>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>Applicants</h2>

      <table border="1" cellPadding="10" cellSpacing="0" style={{ width: '100%', marginTop: '20px' }}>
        <thead>
          <tr>
            <th>User ID</th>
            <th>Application ID</th>
            <th>Status</th>
            <th>Update Status</th>
          </tr>
        </thead>
        <tbody>
          {applicants.map((applicant, index) => (
            <tr key={applicant.id}>
              <td>{applicant.userId || 'N/A'}</td>
              <td>{applicant.applicationId || 'N/A'}</td>
              <td>
                <select
                  value={applicant.status || 'Not Viewed'}
                  onChange={(e) => updateLocalStatus(index, e.target.value)}
                >
                  {statusOptions.map((status) => (
                    <option key={status} value={status}>{status}</option>
                  ))}
                </select>
              </td>
              <td>
                <button onClick={() => updateStatus(index)}>Update</button>
              </td>
            </tr>
          ))}
          {applicants.length === 0 && (
            <tr>
              <td colSpan="4" style={{ textAlign: 'center' }}>
                No applicants found.
              </td>
            </tr>
          )}
        </tbody>
      </table>

      <button style={{ marginTop: '20px' }} onClick={() => navigate('/admin-dashboard', { state: { admin } })}>
        â¬… Back to Dashboard
      </button>
    </div>
  );
}

export default AdminViewApplicants;
