import React, { useEffect, useState } from 'react';
import { auth, db } from '../firebase';
import { useNavigate } from 'react-router-dom';
import {
  collection,
  query,
  where,
  getDocs,
  doc,
  updateDoc
} from 'firebase/firestore';
import '../styles/profile.css'; // modern container
import '../styles/Dashboard.css'; // for navbar & drawer styles

function Profile() {
  const [user, setUser] = useState(null);
  const [userDocId, setUserDocId] = useState('');
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    age: '',
    gender: ''
  });

  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const currentUser = auth.currentUser;
      if (!currentUser) {
        navigate('/login');
        return;
      }

      setUser(currentUser);

      const q = query(collection(db, 'users'), where('email', '==', currentUser.email));
      const snapshot = await getDocs(q);

      if (!snapshot.empty) {
        const docRef = snapshot.docs[0];
        setUserDocId(docRef.id);
        setFormData(prev => ({ ...prev, ...docRef.data() }));
      }
    };

    fetchUserData();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleUpdate = async () => {
    if (!userDocId) return;

    const { name, phone, email, age, gender } = formData;

    if (!name || !phone || !email || !age || !gender) {
      alert('Please fill in all required fields.');
      return;
    }

    try {
      await updateDoc(doc(db, 'users', userDocId), {
        name,
        phone,
        email,
        age,
        gender
      });

      alert('Profile updated successfully!');
    } catch (err) {
      console.error(err);
      alert('Update failed.');
    }
  };

  const logout = async () => {
    await auth.signOut();
    alert('Logged out');
    navigate('/login');
  };

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  if (!user) return <p>Loading profile...</p>;

  return (
    <div>
      {/* Sidebar Drawer */}
      <div className={`side-drawer ${drawerOpen ? 'open' : ''}`}>
        <h3>Navigation</h3>
        <ul>
          <li onClick={() => navigate('/dashboard')}>Home</li>
          <li onClick={() => navigate('/applynew')}>Apply New</li>
          <li onClick={() => navigate('/profile')}>Profile</li>
          <li onClick={() => navigate('/about')}>About</li>
          <li className="logout" onClick={logout}>Logout</li>
        </ul>
      </div>

      {/* Top Navbar */}
      <div className="navbar">
        <div className="left" onClick={toggleDrawer} style={{ cursor: 'pointer' }}>
          <span className="hamburger">&#9776;</span>
          <span className="logo">Epanchayath 360</span>
        </div>
        <div className="right">
          <button onClick={() => navigate('/about')}>About</button>
          <button onClick={() => navigate('/profile')}>Profile</button>
        </div>
      </div>

      {/* Profile Form */}
      <div className="profile-container">
        <h2>My Profile</h2>

        <label>Name</label>
        <input name="name" value={formData.name} onChange={handleChange} />

        <label>Email</label>
        <input name="email" value={formData.email} onChange={handleChange} />

        <label>Phone</label>
        <input name="phone" value={formData.phone} onChange={handleChange} />

        <label>Age</label>
        <input name="age" type="number" value={formData.age} onChange={handleChange} />

        <label>Gender</label>
        <select name="gender" value={formData.gender} onChange={handleChange}>
          <option value="">Select</option>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
        </select>

        <div className="profile-buttons">
          <button className="update-button" onClick={handleUpdate}>
            Update Profile
          </button>
        </div>
      </div>
    </div>
  );
}

export default Profile;
