import React, { useState, useEffect } from 'react';
import {
  collection,
  addDoc,
  onSnapshot,
  query,
  orderBy,
  doc,
  updateDoc,
  deleteDoc,
  serverTimestamp
} from 'firebase/firestore';
import { db } from '../firebase';
import { useNavigate } from 'react-router-dom';

function AdminManageAnnouncements() {
  const [message, setMessage] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [announcements, setAnnouncements] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const q = query(collection(db, 'announcements'), orderBy('startDate', 'desc'));
    const unsub = onSnapshot(q, (snapshot) => {
      setAnnouncements(snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })));
    });
    return unsub;
  }, []);

  const handleAddOrUpdate = async () => {
    if (!message.trim() || !startDate || !endDate) {
      alert('Please fill in all fields.');
      return;
    }

    const data = {
      message: message.trim(),
      startDate: new Date(startDate),
      endDate: new Date(endDate),
      timestamp: serverTimestamp()
    };

    try {
      if (editingId) {
        await updateDoc(doc(db, 'announcements', editingId), data);
        setEditingId(null);
      } else {
        await addDoc(collection(db, 'announcements'), data);
      }

      setMessage('');
      setStartDate('');
      setEndDate('');
    } catch (e) {
      console.error('Error saving announcement:', e);
      alert('Operation failed.');
    }
  };

  const handleEdit = (a) => {
    setMessage(a.message);
    setStartDate(a.startDate.toDate().toISOString().split('T')[0]);
    setEndDate(a.endDate.toDate().toISOString().split('T')[0]);
    setEditingId(a.id);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this announcement?')) {
      await deleteDoc(doc(db, 'announcements', id));
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '700px', margin: 'auto' }}>
      <button
        onClick={() => navigate('/admin-dashboard')}
        style={{
          marginBottom: '20px',
          backgroundColor: '#ccc',
          padding: '8px 16px',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}
      >
        ← Back to Dashboard
      </button>

      <h2>{editingId ? 'Edit Announcement' : 'New Announcement'}</h2>

      <input
        type="date"
        value={startDate}
        onChange={(e) => setStartDate(e.target.value)}
        style={{ marginBottom: '10px', width: '100%' }}
      />
      <input
        type="date"
        value={endDate}
        onChange={(e) => setEndDate(e.target.value)}
        style={{ marginBottom: '10px', width: '100%' }}
      />
      <textarea
        placeholder="Enter announcement message"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        rows={3}
        style={{ width: '100%', marginBottom: '10px' }}
      />

      <button onClick={handleAddOrUpdate}>
        {editingId ? 'Update' : 'Add'} Announcement
      </button>

      <hr style={{ margin: '30px 0' }} />

      <h3>All Announcements</h3>
      {announcements.map((a) => (
        <div key={a.id} style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px' }}>
          <p><strong>Message:</strong> {a.message}</p>
          <p><strong>Start:</strong> {new Date(a.startDate.seconds * 1000).toDateString()}</p>
          <p><strong>End:</strong> {new Date(a.endDate.seconds * 1000).toDateString()}</p>
          <button onClick={() => handleEdit(a)} style={{ marginRight: '10px' }}>Edit</button>
          <button onClick={() => handleDelete(a.id)}>Delete</button>
        </div>
      ))}
    </div>
  );
}

export default AdminManageAnnouncements;
