import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/form.css'; // Optional: reuse styling

function Home() {
  const navigate = useNavigate();

  return (
    <div style={{ textAlign: 'center', marginTop: '100px' }}>
      <h1>E-Grama Panchayath</h1>
      <div style={{ marginTop: '40px' }}>
        <button onClick={() => navigate('/login')} style={{ margin: '10px', padding: '10px 20px' }}>
          Continue as User
        </button>
        <button onClick={() => alert('You can route this to /staff-login later')} style={{ margin: '10px', padding: '10px 20px' }}>
          Continue as Staff / Client
        </button>
      </div>
    </div>
  );
}

export default Home;
