import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { signOut } from 'firebase/auth';
import { auth, db } from '../firebase';
import {
  collection,
  getDocs,
  getDoc,
  doc,
  query,
  where
} from 'firebase/firestore';

import '../styles/Dashboard.css';
import '../styles/NewApplied.css';

function NewApplied() {
  const location = useLocation();
  const navigate = useNavigate();
  const staff = location.state?.staff;

  const [drawerOpen, setDrawerOpen] = useState(false);
  const [category, setCategory] = useState('');
  const [appliedData, setAppliedData] = useState([]);

  const predefinedCategories = [
    'Complaints',
    'Certificate',
    'Financial Support',
    'Customer Grievance',
    'Water Connection',
    'Electricity',
    'Housing Scheme'
  ];

  useEffect(() => {
    if (!staff) {
      navigate('/staff-login');
    }
  }, [staff, navigate]);

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  const logout = async () => {
    try {
      await signOut(auth);
    } catch (err) {
      console.error(err);
    }
    navigate('/staff-login');
  };

  const fetchApplications = async () => {
    if (!category) {
      alert('Please select a category first.');
      return;
    }

    try {
      const appSnap = await getDocs(
        query(collection(db, 'applications'), where('category', '==', category))
      );

      const applicationsMap = {};
      appSnap.forEach(doc => {
        applicationsMap[doc.id] = doc.data();
      });

      const appliedSnap = await getDocs(collection(db, 'Applied'));

      const matched = [];

      for (const docSnap of appliedSnap.docs) {
        const applied = docSnap.data();
        const app = applicationsMap[applied.applicationId];

        if (app && applied.verify !== true) {
          // Fetch user details
          const userRef = doc(db, 'Users', applied.userId);
          const userSnap = await getDoc(userRef);

          let userData = {};
          if (userSnap.exists()) {
            userData = userSnap.data();
          }

          matched.push({
            id: docSnap.id,
            applied,
            application: app,
            user: userData
          });
        }
      }

      setAppliedData(matched);
    } catch (err) {
      console.error(err);
      alert('Error fetching applications.');
    }
  };

  if (!staff) return null;

  return (
    <div>
      {/* Sidebar */}
      <div className={`side-drawer ${drawerOpen ? 'open' : ''}`}>
        <h3>Navigation</h3>
        <ul>
          <li onClick={() => navigate('/staff-dashboard', { state: { staff } })}>Home</li>
          <li onClick={() => navigate('/new-applied', { state: { staff } })}>New Applied</li>
          <li onClick={() => navigate('/staff-profile', { state: { staff } })}>Profile</li>
          <li onClick={() => navigate('/About-')}>About</li>
          <li className="logout" onClick={logout}>Logout</li>
        </ul>
      </div>

      {/* Navbar */}
      <div className="navbar">
        <div className="left" onClick={toggleDrawer} style={{ cursor: 'pointer' }}>
          <span className="hamburger">&#9776;</span>
          <span className="logo">Epanchayath 360</span>
        </div>
        <div className="right">
          <button onClick={() => navigate('/About-')}>About</button>
          <button onClick={() => navigate('/staff-profile', { state: { staff } })}>Profile</button>
        </div>
      </div>

      {/* Main Content */}
      <div className="dashboard-container">
        <div className="new-applied">
          <div className="category-select">
            <label>Select Category: </label>
            <select value={category} onChange={e => setCategory(e.target.value)}>
              <option value="">-- Select Category --</option>
              {predefinedCategories.map((cat, idx) => (
                <option key={idx} value={cat}>{cat}</option>
              ))}
            </select>

            <button onClick={fetchApplications}>View Applications</button>
          </div>

          {appliedData.length > 0 && (
            <div className="applications-list">
              <h3>Applications in "{category}"</h3>
              {appliedData.map((item, index) => (
                <div key={index} className="application-card">
                  <p><strong>Applicant Name:</strong> {item.user.name || 'Unknown'}</p>
                  <p><strong>Applicant ID:</strong> {item.applied.userId}</p>
                  <p><strong>Application:</strong> {item.application.name}</p>
                  <p><strong>Scheme:</strong> {item.application.scheme || 'N/A'}</p>
                  <p><strong>Message:</strong> {item.applied.message}</p>
                  <button
                    onClick={() =>
                      navigate('/viewapp', {
                        state: {
                          applied: item.applied,
                          application: item.application,
                          appliedId: item.id,
                          staffId: staff.id
                        }
                      })
                    }
                  >
                    View
                  </button>
                </div>
              ))}
            </div>
          )}

        </div>
      </div>
    </div>
  );
}

export default NewApplied;
