import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { signOut } from 'firebase/auth';
import { auth } from '../firebase';
import '../styles/Dashboard.css'; // Reusing user dashboard styles
import '../styles/StaffDashboard.css'; // Custom staff styling

function StaffDashboard() {
  const location = useLocation();
  const navigate = useNavigate();
  const staff = location.state?.staff;

  const [drawerOpen, setDrawerOpen] = useState(false);

  useEffect(() => {
    if (!staff) {
      navigate('/staff-login');
    }
  }, [staff, navigate]);

  const logout = async () => {
    try {
      await signOut(auth);
    } catch (err) {
      console.error(err);
    }
    navigate('/staff-login');
  };

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  if (!staff) return null;

  return (
    <div>
      {/* Sidebar */}
      <div className={`side-drawer ${drawerOpen ? 'open' : ''}`}>
        <h3>Navigation</h3>
        <ul>
          <li onClick={() => navigate('/staff-dashboard')}>Home</li>
          <li onClick={() => navigate('/new-applied')}>New Applied</li>
          <li onClick={() => navigate('/profile')}>Profile</li>
          <li onClick={() => navigate('/about')}>About</li>
          <li className="logout" onClick={logout}>Logout</li>
        </ul>
      </div>

      {/* Top Navbar */}
      <div className="navbar">
        <div className="left" onClick={toggleDrawer} style={{ cursor: 'pointer' }}>
          <span className="hamburger">&#9776;</span>
          <span className="logo">Epanchayath 360</span>
        </div>
        <div className="right">
          <button onClick={() => navigate('/about')}>About</button>
          <button onClick={() => navigate('/profile')}>Profile</button>
        </div>
      </div>

      {/* Main Content */}
      <div className="dashboard-container">
        <div className="staff-dashboard">
          <h2>Welcome, {staff.name}</h2>
          <p><strong>Email:</strong> {staff.email}</p>
          <p><strong>Phone:</strong> {staff.phone}</p>

          <div className="dashboard-section">
            <h3>Dashboard Area</h3>
            <p>This section will display applications or actions later.</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default StaffDashboard;
