import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function ViewApp() {
  const location = useLocation();
  const navigate = useNavigate();
  const { applied, application, appliedId, staff } = location.state || {};

  const [showData, setShowData] = useState(false);

  useEffect(() => {
    if (!applied || !application || !appliedId || !staff) {
      alert('Invalid request');
      navigate('/staff-login');
    } else {
      setShowData(true);
    }
  }, [applied, application, appliedId, staff, navigate]);

  if (!showData) return <p>Loading application details...</p>;

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: 'auto' }}>
      <h2>Application Details</h2>

      <div style={{ marginBottom: '30px' }}>
        <h3>ðŸ”· From Application Table</h3>
        <p><strong>Application Name:</strong> {application.name}</p>
        <p><strong>Category:</strong> {application.category}</p>
        <p><strong>Status:</strong> {application.status}</p>
        <p><strong>Description:</strong> {application.message}</p>
      </div>

      <div style={{ marginBottom: '30px' }}>
        <h3>ðŸ”¶ From Applied Table</h3>
        <p><strong>Applicant ID:</strong> {applied.userId}</p>
        <p><strong>Applied ID (doc ID):</strong> {appliedId}</p>
        <p><strong>Message:</strong> {applied.message}</p>
        <p><strong>Applied On:</strong> {applied.timestamp?.toDate?.().toLocaleString() || 'N/A'}</p>
      </div>

      <button
        style={{ marginTop: '30px', padding: '10px 20px' }}
        onClick={() => navigate(-1)}
      >
        Back
      </button>
    </div>
  );
}

export default ViewApp;
