import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db } from '../firebase';
import { collection, getDocs } from 'firebase/firestore';

function ApplyNew() {
  const [applications, setApplications] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // Redirect if not logged in
  useEffect(() => {
    const currentUser = auth.currentUser;
    if (currentUser) {
      setUser(currentUser);
    } else {
      navigate('/login');
    }
  }, [navigate]);

  // Fetch live applications
  useEffect(() => {
    const fetchApplications = async () => {
      const snapshot = await getDocs(collection(db, 'applications'));
      const liveApps = snapshot.docs
        .map(doc => ({ id: doc.id, ...doc.data() }))
        .filter(app => app.status === 'Live');
      setApplications(liveApps);
    };
    fetchApplications();
  }, []);

  const goToApply = (appId) => {
    if (!user) return;
    navigate('/apply', {
      state: { user: user.email, applicationId: appId }
    });
  };

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: 'auto' }}>
      <h2>Available Applications</h2>

      {applications.length === 0 ? (
        <p>No live applications available.</p>
      ) : (
        applications.map(app => (
          <div key={app.id} style={{
            background: '#f9f9f9',
            padding: '15px',
            borderRadius: '5px',
            marginBottom: '10px',
            border: '1px solid #ccc'
          }}>
            <p><strong>{app.name}</strong> - {app.category}</p>
            <p>{app.message}</p>
            <button
              onClick={() => goToApply(app.id)}
              style={{
                padding: '8px 16px',
                backgroundColor: '#4caf50',
                color: '#fff',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Apply
            </button>
          </div>
        ))
      )}
    </div>
  );
}

export default ApplyNew;
