import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db } from '../firebase';
import { collection, getDocs } from 'firebase/firestore';
import { motion } from 'framer-motion';
import '../styles/ApplyNew.css';

function ApplyNew() {
  const [applications, setApplications] = useState([]);
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const currentUser = auth.currentUser;
    if (currentUser) {
      setUser(currentUser);
    } else {
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    const fetchApplications = async () => {
      const snapshot = await getDocs(collection(db, 'applications'));
      const liveApps = snapshot.docs
        .map(doc => ({ id: doc.id, ...doc.data() }))
        .filter(app => app.status === 'Live');
      setApplications(liveApps);
    };
    fetchApplications();
  }, []);

  const goToApply = (appId) => {
    if (!user) return;
    navigate('/apply', {
      state: { user: user.email, applicationId: appId }
    });
  };

  return (
    <motion.div
      className="dashboard-container"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.6 }}
    >
      <motion.h2
        initial={{ y: -30, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ delay: 0.2, duration: 0.6 }}
      >
        Available Applications
      </motion.h2>

      {applications.length === 0 ? (
        <motion.p
          className="no-apps"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.4 }}
        >
          No live applications available.
        </motion.p>
      ) : (
        applications.map((app, index) => (
          <motion.div
            key={app.id}
            className="application-card"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 + 0.3 }}
          >
            <p><strong>{app.name}</strong> - {app.category}</p>
            <p>{app.message}</p>
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
              className="apply-button"
              onClick={() => goToApply(app.id)}
            >
              Apply Now
            </motion.button>
          </motion.div>
        ))
      )}
    </motion.div>
  );
}

export default ApplyNew;
