import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css';

function Home() {
  const [role, setRole] = useState('user');
  const navigate = useNavigate();

  const handleToggle = () => {
    setRole((prev) => (prev === 'user' ? 'staff' : 'user'));
  };

  const handleContinue = () => {
    navigate(role === 'user' ? '/login' : '/staff-login');
  };

  return (
    <div className="home-wrapper">
      <div className="right-container">
        <h1 className="title">E-Grama Panchayath</h1>

        {/* Modern toggle switch with label inside */}
        <div className="modern-toggle-wrapper">
          <label className="modern-switch">
            <input type="checkbox" checked={role === 'staff'} onChange={handleToggle} />
            <span className="modern-slider">
              <span className="switch-label">{role === 'user' ? 'User' : 'Staff'}</span>
            </span>
          </label>
        </div>

        <button className="continue-btn" onClick={handleContinue}>
          Continue
        </button>
      </div>
    </div>
  );
}

export default Home;
